        setInterval(
            ()=>{
                this.setState((state)=>
                {
                    return (
                        {count:this.state+1}
                    )
                })
            }

        ,1000)
    }
    render(){
        return(
            <div>
                <h1>Counter: {this.state.count}</h1>
            </div>
        )
    }
}
